INSERT INTO `opac_news` (`title`, `content`, `lang`, `timestamp`, `expirationdate`, `number`) VALUES ('Willkommen bei Koha','Willkommen bei Koha. Koha ist ein vollst채ndiges integriertes Bibliothekssystem. Es wurde urspr체nglich in Neuseeland von Katipo Communications Ltd entwickelt und ging im Januar 2000 f체r den Horowhenua Library Trust zum ersten mal in Produktion. Koha wird heute von einem internationalen Team aus Support-Dienstleistern und Anwendern weiterentwickelt.','koha','2007-10-29 05:25:58','2099-01-10',1),
('Was kommt jetzt?','Wie geht es weiter, nachdem Sie Koha installiert haben? Hier einige Vorschl채ge:\r\n<ul>\r\n<li><a href=\"http://koha-community.org/documentation/\">Lesen Sie die Koha-Dokumentation</a></li>\r\n<li><a href=\"http://wiki.koha-community.org\">Lesen oder Schreiben Sie im Koha-Wiki</a></li>\r\n<li><a href=\"http://koha-community.org/support/\">Verfolgen oder beteiligen Sie sich an Diskussionen</a></li>\r\n<li><a href=\"http://bugs.koha-community.org\">Melden Sie Bugs</a></li>\r\n<li><a href=\"http://wiki.koha-community.org/wiki/Version_Control_Using_Git\">Erstellen Sie Fehlerkorrekturen mit GIT (Versionskontrollsystem)</a></li>\r\n<li><a href=\"http://koha-community.org/support/\">Chatten Sie mit Koha-Anwendern und -Entwicklern</a></li>\r\n</ul>\r\n','koha','2007-10-29 05:34:45','2099-01-10',2);
